#!/bin/bash

HEAD=''

echo $HEAD | base64 --decode

echo '  <ItemGroup>'
for i in *.cc ; do
  echo '    <ClCompile Include="'"$i"'" />'
done
echo '  </ItemGroup>'
echo '  <ItemGroup>'
for i in *.h ; do
  echo '    <ClInclude Include="'"$i"'" />'
done
echo '  </ItemGroup>'

echo '  <Import Project="$(VCTargetsPath)\Microsoft.Cpp.targets" />'
echo '  <ImportGroup Label="ExtensionTargets">'
echo '  </ImportGroup>'
echo '</Project>'
